import React, {useState} from 'react';

import Depenses from "./components/Depenses/Depenses";
import NouvelleDepense from './components/NouvelleDepense/NouvelleDepense';

const DUMMY_EXPENSES = [
  {
    id: 'e1',
    title: 'Assurance voiture', 
    amount: 107.5, 
    date: new Date(2022, 2, 28)
  },
  {
    id: 'e2',
    title: 'Frais vétérinaire', 
    amount: 355.7, 
    date: new Date(2021, 9, 15)
  },
  {
    id: 'e3',
    title: 'Chauffage', 
    amount: 87.9, 
    date: new Date(2020, 11, 5)
  },
  {
    id: 'e4',
    title: 'Nourriture', 
    amount: 200.46, 
    date: new Date(2020, 6, 16)
  },
];

const App = () => {

  const [expenses, setExpenses] = useState(DUMMY_EXPENSES);

  const addExpenseHandler = (expense) => {
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses]
    }); //peupler l'array avec celui dejà existant grâce au spread + passer en fonction pour se baser sur ce qui existe dejà
  };
  return (
    <div>
      <NouvelleDepense onAddExpense={addExpenseHandler}/>
      <Depenses items={expenses}/>
    </div>
  );
}

export default App;
